#!/bin/bash

DRF_LOGGER_PYTHON_VERSION="3"
DRF_LOGGER_REPO_NAME="drf-logger"
DRF_LOGGER_GIT_REPO="git@github.com:0PEIN0/$DRF_LOGGER_REPO_NAME.git"
DRF_LOGGER_PROJECT_NAME="drflogger"
DRF_LOGGER_CELERY_PROJECT_NAME="drf_logger"
DRF_LOGGER_PROJECT_NAME_UNDERSCORE="drf_logger"
DRF_LOGGER_PROJECT_NAME_CAMEL_CASE="drfLogger"
DRF_LOGGER_LOCAL_PORT="8066"
DRF_LOGGER_DJANGO_APP_NAMES="utility"
DRF_LOGGER_POSTGRES_USER="postgres"
DRF_LOGGER_CELERY_FLOWER_HOST="127.0.0.1"
DRF_LOGGER_CELERY_FLOWER_PORT="5566"

DRF_LOGGER_DJANGO_OPERATIONS="makemigrations migrate"
DRF_LOGGER_INIT_REQUIREMENTS_FILE_NAME='requirements.txt'
DRF_LOGGER_PROJECT_ROOT_FOLDER="$SYSTEM_ROOT_GIT_REPO_FOLDER/$DRF_LOGGER_REPO_NAME"
DRF_LOGGER_VIRTUAL_ENVIRONMENT_NAME="${DRF_LOGGER_PROJECT_NAME_CAMEL_CASE}Env"
DRF_LOGGER_INIT_DB_FILE_NAME="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}-reset-db.sql"
DRF_LOGGER_POSTGRES_USER_PASSWORD_RESET_FILE_NAME="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}-postgres-password-reset.sql"
DRF_LOGGER_INIT_DATA_LOAD_PYTHON_FILE_NAME="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_init_data_load.py"
DRF_LOGGER_VIRTUAL_ENVIRONMENT_FOLDER="$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER/$DRF_LOGGER_VIRTUAL_ENVIRONMENT_NAME"
DRF_LOGGER_INIT_POSTGRES_DB_FILE_PATH="$BASH_TEMP_FOLDER/$DRF_LOGGER_INIT_DB_FILE_NAME"
DRF_LOGGER_INIT_POSTGRES_DB_USER_PASSWORD_FILE_PATH="$BASH_TEMP_FOLDER/$DRF_LOGGER_POSTGRES_USER_PASSWORD_RESET_FILE_NAME"
DRF_LOGGER_INIT_DATA_LOAD_PYTHON_FILE_PATH="$DRF_LOGGER_PROJECT_ROOT_FOLDER/deployment/$DRF_LOGGER_INIT_DATA_LOAD_PYTHON_FILE_NAME"

if [ -f $BASH_HELPER_GIT_FOLDER/django-project-core.sh ]; then
    . $BASH_HELPER_GIT_FOLDER/django-project-core.sh
fi;

alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bc="djangoBranchChange $DRF_LOGGER_PROJECT_NAME_UNDERSCORE "
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bc_full_reset="djangoBranchChangeWithFullReset $DRF_LOGGER_PROJECT_NAME_UNDERSCORE "
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bc_full_reset_and_run="djangoBranchChangeWithFullResetAndRun $DRF_LOGGER_PROJECT_NAME_UNDERSCORE "
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bcd="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bc develop"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bcd_full_reset="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bc_full_reset develop"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bcd_full_reset_and_run="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bc_full_reset_and_run develop"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bcd_run="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_bcd && ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_run"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_db_shell="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve && ./manage.py shell"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_dir="goToDir \"$DRF_LOGGER_PROJECT_ROOT_FOLDER\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_frr="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_full_reset && ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_run"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_full_reset="djangoFullReset $DRF_LOGGER_PROJECT_NAME_UNDERSCORE"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_gf="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_dir && git_f"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_git_setup="djangoGitSetup $DRF_LOGGER_GIT_REPO"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_init_data_load="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve && ./manage.py shell < $DRF_LOGGER_INIT_DATA_LOAD_PYTHON_FILE_PATH"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_last_10_commit_hash="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_dir && git log -10 --pretty=format:\"%h\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_loc="cloc \"$DRF_LOGGER_PROJECT_ROOT_FOLDER\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_reinitiate="djangoReinitiate \"$DRF_LOGGER_PROJECT_ROOT_FOLDER\" \"$DRF_LOGGER_DJANGO_APP_NAMES\" $DRF_LOGGER_INIT_DATA_LOAD_PYTHON_FILE_PATH \"$DRF_LOGGER_DJANGO_OPERATIONS\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_reinitiate_clean="djangoReinitiateClean \"$DRF_LOGGER_PROJECT_ROOT_FOLDER\" \"$DRF_LOGGER_DJANGO_OPERATIONS\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_reset_clean="djangoResetWithoutMigrationClean $DRF_LOGGER_PROJECT_NAME_UNDERSCORE $DRF_LOGGER_INIT_DATA_LOAD_PYTHON_FILE_PATH"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_postgres_shell="psql -U $DRF_LOGGER_POSTGRES_USER"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_postgres_user_password_reset="postgresPasswordReset \"$DRF_LOGGER_INIT_POSTGRES_DB_USER_PASSWORD_FILE_PATH\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_process_id="lsof -i:${DRF_LOGGER_LOCAL_PORT} | awk '{print $2}'"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_psql_reset="djangoPsqlReset $DRF_LOGGER_POSTGRES_USER \"$DRF_LOGGER_INIT_POSTGRES_DB_FILE_PATH\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_reset="djangoReset $DRF_LOGGER_PROJECT_NAME_UNDERSCORE"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_rr="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_reset && ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_run"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_rt="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_reset && ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_test"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_run="djangoRun $DRF_LOGGER_PROJECT_NAME_UNDERSCORE $DRF_LOGGER_PROJECT_NAME_UNDERSCORE $DRF_LOGGER_LOCAL_PORT"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_run_production="djangoProductionRun $DRF_LOGGER_PROJECT_NAME_UNDERSCORE $DRF_LOGGER_PROJECT_NAME $DRF_LOGGER_LOCAL_PORT"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_run_generic_deployment="djangoDeploymentOperations $DRF_LOGGER_PROJECT_NAME_UNDERSCORE"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_start_celery="startCeleryWorkers $DRF_LOGGER_CELERY_PROJECT_NAME $DRF_LOGGER_CELERY_FLOWER_HOST $DRF_LOGGER_CELERY_FLOWER_PORT"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_stop_processes="djangoStopProcesses $DRF_LOGGER_LOCAL_PORT $DRF_LOGGER_CELERY_PROJECT_NAME"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_test="djangoTest $DRF_LOGGER_PROJECT_NAME_UNDERSCORE"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve="djangoUseVe \"$DRF_LOGGER_VIRTUAL_ENVIRONMENT_FOLDER\" $DRF_LOGGER_PROJECT_NAME_UNDERSCORE"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve_check="checkVirtualPythonEnvironmentFolder \"$DRF_LOGGER_VIRTUAL_ENVIRONMENT_FOLDER\" \"$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER\" $DRF_LOGGER_VIRTUAL_ENVIRONMENT_NAME"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve_clear="djangoVeClear \"$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER\" $DRF_LOGGER_VIRTUAL_ENVIRONMENT_NAME"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve_init="pythonVeVersionDecider \"$DRF_LOGGER_VIRTUAL_ENVIRONMENT_FOLDER\" \"$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER\" $DRF_LOGGER_VIRTUAL_ENVIRONMENT_NAME $DRF_LOGGER_PROJECT_NAME \"$DRF_LOGGER_PYTHON_VERSION\""
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve_installs="djangoDefaultSetup \"$DRF_LOGGER_PROJECT_ROOT_FOLDER\" $DRF_LOGGER_INIT_REQUIREMENTS_FILE_NAME $DRF_LOGGER_PROJECT_NAME_UNDERSCORE"
alias ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_installs="${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve_init && cd \"$SYSTEM_ROOT_GIT_REPO_FOLDER\" && git clone ${DRF_LOGGER_GIT_REPO} && ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_ve_installs && ${DRF_LOGGER_PROJECT_NAME_UNDERSCORE}_dir"
