#!/bin/bash

EXAMPLE_PYTHON_VERSION="3"
EXAMPLE_REPO_NAME="example_repo_name"
EXAMPLE_GIT_REPO="git@github.com:GITHUB_USER/$EXAMPLE_REPO_NAME.git"
EXAMPLE_PROJECT_NAME="example_project_name"
EXAMPLE_CELERY_PROJECT_NAME="example_project_name"
EXAMPLE_PROJECT_NAME_UNDERSCORE="example_project_name"
EXAMPLE_PROJECT_NAME_CAMEL_CASE="example_project_name"
EXAMPLE_LOCAL_PORT="8066"
EXAMPLE_DJANGO_APP_NAMES="DJANGO_APP_NAMES_SPACE_SEPARATED"
EXAMPLE_POSTGRES_USER="postgres_user_name"
EXAMPLE_CELERY_FLOWER_HOST="127.0.0.1"
EXAMPLE_CELERY_FLOWER_PORT="5566"

EXAMPLE_DJANGO_OPERATIONS="makemigrations migrate"
EXAMPLE_INIT_REQUIREMENTS_FILE_NAME='requirements.txt'
EXAMPLE_PROJECT_ROOT_FOLDER="$SYSTEM_ROOT_GIT_REPO_FOLDER/$EXAMPLE_REPO_NAME"
EXAMPLE_VIRTUAL_ENVIRONMENT_NAME="${EXAMPLE_PROJECT_NAME_CAMEL_CASE}Env"
EXAMPLE_INIT_DB_FILE_NAME="${EXAMPLE_PROJECT_NAME_UNDERSCORE}-reset-db.sql"
EXAMPLE_POSTGRES_USER_PASSWORD_RESET_FILE_NAME="${EXAMPLE_PROJECT_NAME_UNDERSCORE}-postgres-password-reset.sql"
EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_NAME="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_init_data_load.py"
EXAMPLE_VIRTUAL_ENVIRONMENT_FOLDER="$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER/$EXAMPLE_VIRTUAL_ENVIRONMENT_NAME"
EXAMPLE_INIT_POSTGRES_DB_FILE_PATH="$BASH_TEMP_FOLDER/$EXAMPLE_INIT_DB_FILE_NAME"
EXAMPLE_INIT_POSTGRES_DB_USER_PASSWORD_FILE_PATH="$BASH_TEMP_FOLDER/$EXAMPLE_POSTGRES_USER_PASSWORD_RESET_FILE_NAME"
EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH="$EXAMPLE_PROJECT_ROOT_FOLDER/deployment/$EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_NAME"

EXAMPLE_POSTGRES_SCRIPT_PATH="$BASH_TEMP_FOLDER"
EXAMPLE_INIT_POSTGRES_DB_FILE_PATH="$EXAMPLE_POSTGRES_SCRIPT_PATH/$EXAMPLE_INIT_DB_FILE_NAME"
EXAMPLE_INIT_POSTGRES_DB_USER_PASSWORD_FILE_PATH="$EXAMPLE_POSTGRES_SCRIPT_PATH/$EXAMPLE_POSTGRES_USER_PASSWORD_RESET_FILE_NAME"
EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH="$EXAMPLE_POSTGRES_SCRIPT_PATH/$EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_NAME"

alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bc="djangoBranchChange $EXAMPLE_PROJECT_NAME_UNDERSCORE "
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bc_full_reset="djangoBranchChangeWithFullReset $EXAMPLE_PROJECT_NAME_UNDERSCORE "
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bc_full_reset_and_run="djangoBranchChangeWithFullResetAndRun $EXAMPLE_PROJECT_NAME_UNDERSCORE "
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bcd="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bc develop"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bcd_full_reset="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bc_full_reset develop"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bcd_full_reset_and_run="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bc_full_reset_and_run develop"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bcd_run="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_bcd && ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_run"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_db_shell="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve && ./manage.py shell"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_dir="goToDir \"$EXAMPLE_PROJECT_ROOT_FOLDER\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_frr="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_full_reset && ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_run"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_full_reset="djangoFullReset $EXAMPLE_PROJECT_NAME_UNDERSCORE"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_gf="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_dir && git_f"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_git_setup="djangoGitSetup $EXAMPLE_GIT_REPO"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_init_data_load="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve && ./manage.py shell < $EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_last_10_commit_hash="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_dir && git log -10 --pretty=format:\"%h\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_loc="cloc \"$EXAMPLE_PROJECT_ROOT_FOLDER\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reinitiate="djangoReinitiate \"$EXAMPLE_PROJECT_ROOT_FOLDER\" \"$EXAMPLE_DJANGO_APP_NAMES\" $EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH \"$EXAMPLE_DJANGO_OPERATIONS\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reinitiate_clean="djangoReinitiateClean \"$EXAMPLE_PROJECT_ROOT_FOLDER\" \"$EXAMPLE_DJANGO_OPERATIONS\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reset_clean="djangoResetWithoutMigrationClean $EXAMPLE_PROJECT_NAME_UNDERSCORE $EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_postgres_shell="psql -U $EXAMPLE_POSTGRES_USER"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_postgres_user_password_reset="postgresPasswordReset \"$EXAMPLE_INIT_POSTGRES_DB_USER_PASSWORD_FILE_PATH\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_process_id="lsof -i:${EXAMPLE_LOCAL_PORT} | awk '{print $2}'"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_psql_reset="djangoPsqlReset $EXAMPLE_POSTGRES_USER \"$EXAMPLE_INIT_POSTGRES_DB_FILE_PATH\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reset="djangoReset $EXAMPLE_PROJECT_NAME_UNDERSCORE"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_rr="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reset && ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_run"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_rt="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reset && ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_test"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_run="djangoRun $EXAMPLE_PROJECT_NAME_UNDERSCORE $EXAMPLE_PROJECT_NAME_UNDERSCORE $EXAMPLE_LOCAL_PORT"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_run_production="djangoProductionRun $EXAMPLE_PROJECT_NAME_UNDERSCORE $EXAMPLE_PROJECT_NAME $EXAMPLE_LOCAL_PORT"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_run_generic_deployment="djangoDeploymentOperations $EXAMPLE_PROJECT_NAME_UNDERSCORE"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_start_celery="startCeleryWorkers $EXAMPLE_CELERY_PROJECT_NAME $EXAMPLE_CELERY_FLOWER_HOST $EXAMPLE_CELERY_FLOWER_PORT"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_stop_processes="djangoStopProcesses $EXAMPLE_LOCAL_PORT $EXAMPLE_CELERY_PROJECT_NAME"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_test="djangoTest $EXAMPLE_PROJECT_NAME_UNDERSCORE"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve="djangoUseVe \"$EXAMPLE_VIRTUAL_ENVIRONMENT_FOLDER\" $EXAMPLE_PROJECT_NAME_UNDERSCORE"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve_check="checkVirtualPythonEnvironmentFolder \"$EXAMPLE_VIRTUAL_ENVIRONMENT_FOLDER\" \"$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER\" $EXAMPLE_VIRTUAL_ENVIRONMENT_NAME"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve_clear="djangoVeClear \"$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER\" $EXAMPLE_VIRTUAL_ENVIRONMENT_NAME"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve_init="pythonVeVersionDecider \"$EXAMPLE_VIRTUAL_ENVIRONMENT_FOLDER\" \"$SYSTEM_ROOT_VIRTUAL_PYTHON_ENVIRONMENT_FOLDER\" $EXAMPLE_VIRTUAL_ENVIRONMENT_NAME $EXAMPLE_PROJECT_NAME \"$EXAMPLE_PYTHON_VERSION\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve_installs="djangoDefaultSetup \"$EXAMPLE_PROJECT_ROOT_FOLDER\" $EXAMPLE_INIT_REQUIREMENTS_FILE_NAME $EXAMPLE_PROJECT_NAME_UNDERSCORE"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_installs="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve_init && cd ~  && cd \"$SYSTEM_ROOT_GIT_REPO_FOLDER\" && git clone ${EXAMPLE_GIT_REPO} && ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve_installs && ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_dir"

alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_init_data_load="${EXAMPLE_PROJECT_NAME_UNDERSCORE}_ve && ./manage.py shell < $EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_postgres_user_password_reset="postgresPasswordReset $EXAMPLE_INIT_POSTGRES_DB_USER_PASSWORD_FILE_PATH"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_psql_reset="djangoPsqlReset $EXAMPLE_POSTGRES_USER $EXAMPLE_INIT_POSTGRES_DB_FILE_PATH"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reinitiate="djangoReinitiate \"$EXAMPLE_PROJECT_ROOT_FOLDER\" \"$EXAMPLE_DJANGO_APP_NAMES\" $EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH \"$EXAMPLE_DJANGO_OPERATIONS\""
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_reset_clean="djangoResetWithoutMigrationClean $EXAMPLE_PROJECT_NAME_UNDERSCORE $EXAMPLE_INIT_DATA_LOAD_PYTHON_FILE_PATH"
alias ${EXAMPLE_PROJECT_NAME_UNDERSCORE}_start_celery="startCeleryWorkers $EXAMPLE_CELERY_PROJECT_NAME $EXAMPLE_CELERY_FLOWER_HOST $EXAMPLE_CELERY_FLOWER_PORT"
